import { View as GraphicsView } from "expo-graphics";
import ExpoTHREE, { THREE } from "expo-three";
import React from "react";
import { Button, Alert, Text, View, StyleSheet } from "react-native";

export default class ThreeChart extends React.Component {
  UNSAFE_componentWillMount() {
    THREE.suppressExpoWarnings();
  }

  render() {
    // Create an `ExpoGraphics.View` covering the whole screen, tell it to call our
    // `onContextCreate` function once it's initialized.
    return (
      <>
        <View styles={styles.container}>
          <Text>
            The title and onPress handler are required. It is recommended to set
            accessibilityLabel to help make your ThreeChart usable by everyone.
          </Text>
          <Button
            title="Press me"
            onPress={() => Alert.alert("Simple Button pressed")}
          />
        </View>
        <GraphicsView
          onContextCreate={this.onContextCreate}
          onRender={this.onRender}
        />
        <Text style={styles.title}>Tráº§n Vi Khan</Text>
      </>
    );
  }

  // This is called by the `ExpoGraphics.View` once it's initialized
  onContextCreate = async ({
    gl,
    canvas,
    width,
    height,
    scale: pixelRatio,
  }) => {
    this.renderer = new ExpoTHREE.Renderer({ gl, pixelRatio, width, height });
    this.renderer.setClearColor(0x000000);
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    this.camera.position.z = 5;
    const geometry = new THREE.BoxGeometry(1, 1, 1);

    const material = new THREE.MeshPhongMaterial({
      color: 0x2ecc71,
    });

    this.cube = new THREE.Mesh(geometry, material);
    this.scene.add(this.cube);

    this.scene.add(new THREE.AmbientLight(0x404040));

    const light = new THREE.DirectionalLight(0xffffff, 0.7);
    light.position.set(3, 3, 3);
    this.scene.add(light);
  };

  onRender = (delta) => {
    this.cube.rotation.x += 2 * delta;
    this.cube.rotation.y += 1 * delta;
    this.renderer.render(this.scene, this.camera);
  };
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    marginHorizontal: 16,
    backgroundColor: "#00000"
  },
  title: {
    textAlign: "center",
    fontSize: 50,
    marginVertical: 8,
  },
  fixToText: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: "#737373",
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
});
